
CREATE TABLE `franchises3` (
                               `idx` int(11) unsigned NOT NULL,
                               `status` enum('draft','active','hidden') NOT NULL DEFAULT 'active',
                               `category_id` int(11) DEFAULT NULL,
                               `author_id` int(11) DEFAULT NULL,
                               `naver_id` int(11) NOT NULL,
                               `name` varchar(50) NOT NULL DEFAULT '',
                               `tel` varchar(20) DEFAULT NULL,
                               `category` varchar(200) DEFAULT NULL,
                               `address` varchar(150) DEFAULT '',
                               `roadAddress` varchar(150) DEFAULT NULL,
                               `abbr_address` varchar(150) DEFAULT NULL,
                               `reviewCount` int(11) DEFAULT NULL,
                               `thum_url` tinytext,
                               `x` double DEFAULT NULL,
                               `y` double DEFAULT NULL,
                               `bizhourInfo` text,
                               `menuInfo` text,
                               `coupon` tinyint(1) DEFAULT NULL,
                               `created_at`         VARCHAR(100)    NOT NULL DEFAULT '0000-00-00 00:00:00' COMMENT '생성일',
                               `pos` smallint(4) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;




CREATE TABLE `cheese`.`store3` (
                                   `id` INT UNSIGNED NOT NULL AUTO_INCREMENT,
                                   `name` VARCHAR(128) NOT NULL COMMENT '매장명',
                                   `tel` VARCHAR(20) DEFAULT NULL COMMENT '매장 전화번호',
                                   `zip_code` VARCHAR(10) DEFAULT NULL COMMENT '우편번호',
                                   `address` VARCHAR(256) DEFAULT NULL COMMENT '주소',
                                   `address_extra` VARCHAR(256) DEFAULT NULL COMMENT '상세주소',
                                   `location` point NOT NULL COMMENT '위도/경도',
                                   `thumbnail` VARCHAR(256) NOT NULL COMMENT 'host 제외한 URL',
                                   `state_flag` CHAR(1) NOT NULL DEFAULT 'A' COMMENT '사용 상태(A:활성,W:승인대기,W:중지)',
                                   `create_date` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '생성일',
                                   `update_date` TIMESTAMP NULL COMMENT '수정일' DEFAULT NULL,
                                   `delete_date` TIMESTAMP NULL COMMENT '삭제일',
                                   PRIMARY KEY (`id`)
) COMMENT = '매장테이블'
    ENGINE = InnoDb DEFAULT CHARSET=utf8;


